import React, { useState } from 'react';

const Alerts = ({ sensors, onSetAlert }) => {
  const [thresholds, setThresholds] = useState({});

  const handleChange = (sensorId, field, value) => {
    setThresholds((prev) => ({
      ...prev,
      [sensorId]: {
        ...prev[sensorId],
        [field]: parseFloat(value),
      },
    }));
  };

  const handleSubmit = (e) => {
    e.preventDefault();
    onSetAlert(thresholds);
  };

  return (
    <div>
      <h2>Set Alerts</h2>
      <form onSubmit={handleSubmit}>
        {sensors.map((sensor) => (
          <div key={sensor.id}>
            <label>{sensor.label}</label>
            <div>
              <input
                type="number"
                placeholder="Min"
                onChange={(e) => handleChange(sensor.id, 'min', e.target.value)}
              />
              <input
                type="number"
                placeholder="Max"
                onChange={(e) => handleChange(sensor.id, 'max', e.target.value)}
              />
            </div>
          </div>
        ))}
        <button type="submit">Set Alerts</button>
      </form>
    </div>
  );
};

export default Alerts;
