import React, { useState } from 'react';

const MeatAssignment = ({ probes, meatTypes, onAssignMeat }) => {
  const [selectedProbe, setSelectedProbe] = useState('');
  const [selectedMeat, setSelectedMeat] = useState('');
  const [weight, setWeight] = useState('');

  const handleAssign = (e) => {
    e.preventDefault();
    if (!selectedProbe || !selectedMeat || !weight) {
      alert('Please fill out all fields.');
      return;
    }

    onAssignMeat({
      probe: selectedProbe,
      meat: selectedMeat,
      weight: parseFloat(weight),
    });

    // Reset form
    setSelectedProbe('');
    setSelectedMeat('');
    setWeight('');
  };

  return (
    <div>
      <h2>Assign Meat to Probes</h2>
      <form onSubmit={handleAssign}>
        <label>
          Select Probe:
          <select
            value={selectedProbe}
            onChange={(e) => setSelectedProbe(e.target.value)}
          >
            <option value="">Select a probe</option>
            {probes.map((probe) => (
              <option key={probe.id} value={probe.id}>
                {probe.label}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Select Meat Type:
          <select
            value={selectedMeat}
            onChange={(e) => setSelectedMeat(e.target.value)}
          >
            <option value="">Select meat</option>
            {meatTypes.map((meat) => (
              <option key={meat.id} value={meat.name}>
                {meat.name}
              </option>
            ))}
          </select>
        </label>
        <br />
        <label>
          Enter Weight (lbs):
          <input
            type="number"
            value={weight}
            onChange={(e) => setWeight(e.target.value)}
            placeholder="e.g., 5.5"
          />
        </label>
        <br />
        <button type="submit">Assign</button>
      </form>
    </div>
  );
};

export default MeatAssignment;
